<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Articles extends CI_Controller {    function __construct()    {        parent::__construct();        $this->load->library(array('ion_auth','form_validation'));        if (!$this->ion_auth->logged_in()){            redirect('auth', 'refresh');        }        $this->load->model('admin/Admin_model');    }    public function dashboard() {        $this->template->show('admin','dashboard');    }    public function all(){        $data['articles'] = $this->Admin_model->get_articles();        $this->template->show('admin','articles',$data);    }    public function edit($id){        $data['article_data'] = $this->Admin_model->show_article($id);        $this->template->show('admin','edit_article', $data);    }    public function add(){        $this->template->show('admin','add_article');    }    public function addArticle(){        $post = $this->input->post();        if ($this->Admin_model->add_article($post)) {            $this->session->set_flashdata('msg', 'Запись успешно добавлена');            redirect('/admin/articles', 'refresh');        }else {            echo 'чтото пошло не так...';        }    }    public function saveArticle(){        $post = $this->input->post();        if ($this->Admin_model->edit_article($post)){            $this->session->set_flashdata('msg', 'Запись успешно изменена');            redirect('/admin/articles', 'refresh');        }else {            echo 'чтото пошло не так...';        }    }    public function delete($id){        if($this->Admin_model->delete_article($id)){            $this->session->set_flashdata('msg', 'Запись успешно удалена');            redirect('admin/articles', 'refresh');        }else{            echo 'чтото пошло не так...';        }    }}